class MainApp{constructor(){this.init()}init(){window.mapLoader=new MapLoader;document.addEventListener("polygonsLoaded",e=>{const polygons=e.detail.polygons;const equipos=e.detail.equipos;window.searchEngine=new SearchEngine(polygons);new GPSTracker;new QRScanner;this.setupMapSelector()})}setupMapSelector(){document.getElementById("mapSelector").addEventListener("change",e=>{window.mapLoader.changeBaseMap(e.target.value)})}}document.addEventListener("DOMContentLoaded",()=>{new MainApp});