class MapLoader{constructor(){this.map=null;this.baseMaps={};this.polygons=null;this.equiposLayer=null;this.init()}init(){this.map=L.map("map").setView([22.43,-97.89],10);this.setupBaseMaps();this.loadGeoJSON()}setupBaseMaps(){this.baseMaps={osm:L.tileLayer("https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png",{attribution:"© OpenStreetMap"}),satelite:L.tileLayer("https://{s}.google.com/vt/lyrs=s&x={x}&y={y}&z={z}",{subdomains:["mt0","mt1","mt2","mt3"],attribution:"© Google"}),hibrido:L.tileLayer("https://{s}.google.com/vt/lyrs=s,h&x={x}&y={y}&z={z}",{subdomains:["mt0","mt1","mt2","mt3"],attribution:"© Google"})};this.baseMaps["osm"].addTo(this.map)}loadGeoJSON(){Promise.all([fetch("datos.geojson").then(r=>{if(!r.ok)throw new Error(`Error datos.geojson: ${r.status}`);return r.json()}),fetch("equipos.geojson").then(r=>{if(!r.ok)throw new Error(`Error equipos.geojson: ${r.status}`);return r.json()})]).then(([datosBusqueda,datosEquipos])=>{console.log("Capa búsqueda cargada:",datosBusqueda);console.log("Capa equipos cargada:",datosEquipos);const datosBusquedaFiltrados={...datosBusqueda,features:datosBusqueda.features.filter(f=>f.geometry&&f.geometry.coordinates&&f.geometry.coordinates.length>0)};const datosEquiposFiltrados={...datosEquipos,features:datosEquipos.features.filter(f=>f.geometry&&f.geometry.coordinates&&f.geometry.coordinates.length>0)};console.log(`Features válidos - Búsqueda: ${datosBusquedaFiltrados.features.length}/${datosBusqueda.features.length}`);console.log(`Features válidos - Equipos: ${datosEquiposFiltrados.features.length}/${datosEquipos.features.length}`);this.equiposLayer=L.geoJSON(datosEquiposFiltrados,{style:{color:"#ff00ff",weight:3,fillColor:"transparent",fillOpacity:0},onEachFeature:(feature,layer)=>{this.addTeamLabel(feature,layer)}}).addTo(this.map);this.polygons=L.geoJSON(datosBusquedaFiltrados,{style:{color:"blue",weight:1,fillColor:"transparent",fillOpacity:0},onEachFeature:(feature,layer)=>{const clave=feature.properties.clavemnz||"N/A";layer.bindPopup(`<strong style="font-size: 14px; color: #007bff;">${clave}</strong>`,{className:"minimal-popup",maxWidth:150,autoPan:false})}}).addTo(this.map);this.polygons.bringToFront();document.dispatchEvent(new CustomEvent("polygonsLoaded",{detail:{polygons:this.polygons,equipos:this.equiposLayer}}))}).catch(error=>{console.error("Error cargando GeoJSON:",error);document.getElementById("result").innerHTML="Error cargando datos: "+error.message})}addTeamLabel(feature,layer){try{if(!layer||!layer.getBounds)return;const bounds=layer.getBounds();if(!bounds||!bounds.isValid())return;var center=bounds.getCenter();const teamNumber=feature.properties.Name||feature.properties.name||"N/A";var label=L.marker(center,{icon:L.divIcon({className:"team-label",html:`<div style="font-size: 16px; font-weight: bold; color: #ff00ff; text-shadow: 2px 2px 4px white;">${teamNumber}</div>`,iconSize:[40,25]}),interactive:false}).addTo(this.map)}catch(error){console.warn("Error creando etiqueta de equipo:",error)}}changeBaseMap(mapType){Object.values(this.baseMaps).forEach(layer=>this.map.removeLayer(layer));this.baseMaps[mapType].addTo(this.map)}getMap(){return this.map}getPolygons(){return this.polygons}}